$('.owl-carousel').owlCarousel({
	loop:true,
    margin:25,
	dots: true,
	autoplay:true, //Автозапуск слайдера
    smartSpeed:1000, //Время движения слайда
    autoplayTimeout:5000, //Время смены слайда
	nav: true,
	navSpeed:1000,
	navText: ["<img src=\"img/left-button.png\" alt=\"left\"/>","<img src=\"img/right-button.png\" alt=\"right\"/>"],
    responsive:{
        0:{
            items:1
        },
        600:{
            items:2
        },
        1000:{
            items:3
        }
    }
})